import React from 'react'
import {connect} from 'react-redux';
import { Link, Route, Redirect } from "react-router-dom";
import {createPageReducer} from '../_reducers/createPageReducer';


import {Template} from '../Template';
import { Menu } from '../Menu';
import {store} from '../_helpers/store'
import { userActions } from '../_actions';
import { history } from '../_helpers';



class CreatePage extends React.Component{ 

    constructor() {
        super();

        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        this.props.dispatch(userActions.getAll());
    }

    handleSubmit(e){
        e.preventDefault();
        const title = this.getTitle.value;
        const message =  this.getMessage.value;
        const data = {
          id: new Date(),
          title,
          message
        }


        store.dispatch({
            type:'CREATE_PAGE',
            data});
          this.getTitle.value = '';
          this.getMessage.value = '';


        //   console.log(store.getState().createPageReducer)


    }




    render() {
        const { user, users } = this.props;

        return (
            (  user   )?( 
            <Template>


            <div className={"createPage col-6 col-lg-6 col-sm-6 "}>
            <div >
                <h2>create page</h2>
                <form name="createPageForm" onSubmit={this.handleSubmit} >

                    title:<br></br>
                    <input ref={(input)=>this.getTitle = input} name="title" type= "text" required/>
                    <br></br>
                    content:<br></br>
                    <textarea   ref={(input)=>this.getMessage = input}   type="textarea"/>
                    <br></br>

                    <button>submit</button>

                </form>

            </div>
            </div>


            </Template>



        ):(
            // history.push('/login');
            <Redirect to="/login" />
        )


        )
    }



}

function mapStateToProps(state) {
    const { users, authentication } = state;
    const { user } = authentication;
    return {
        user,
        users
    };
}

const connectedCreatePage = connect(mapStateToProps)(CreatePage);
export { connectedCreatePage as CreatePage };
// export {CreatePage};
// export default connect()(CreatePage);
