import React from 'react';
import { connect } from 'react-redux';

import { NavLink } from "react-router-dom";
import './menu.css'
import {store} from '../_helpers/store';

const selectedStyle = {
    background: "rgb(197, 191, 191",
    color: '#fff'
  }
//  const pagesArray = store.getState().createPageReducer;



  class Menu extends React.Component{



    componentDidMount(){
      this.unsubscribe = store.subscribe(()=>this.setState({}));
  }
  componentWillUnmount(){
      this.unsubscribe();
  }


    render(){

      const {createPageReducer} = this.props;
        return(
            <nav className='main-menu col-md-4 col-md-offset-0'>



      { ( store.getState().createPageReducer != 0 )? ( 
        store.getState().createPageReducer.map((item,key) => (
          <NavLink key={key}  activeStyle = {selectedStyle} to={"/pages/"+ item.title +","+ item.message}> {item.title} </NavLink>
        ))):( "" )}




            <NavLink to='/createPage' activeStyle = {selectedStyle}>create new page</NavLink>
          </nav>


        )
    }
  }

  const mapStateToProps = (state) => {
    return {
        posts: state
    }
}
// export default connect(mapStateToProps)(Menu);

  export {Menu};

  export default connect()( Menu );
