import React, { Component } from 'react';


/*Menu Component 
-------------------------------------------------*/

class Menu extends React.Component {
    constructor() {
        super();



        this.state = {
            pages: [{
                pageId: "",
                title:"",
                content:"",

             }],

             //add a state  nowPage 

        };

        // this.onChangePage = this.onChangePage.bind(this);
        // this.createPage = this.createPage.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(e){
        e.preventDefault();
         var joined = this.state.pages.concat({pageId:"skldj"});
        this.setState({

           pages: joined 

            //setState : push new value to pages array.

        })

    }



    render() {

        const {pageId, title, content} = this.state.pages;

        return (
            <div>
                <div className="container">
                    <div className="text-center">


                    <div>{this.state.pages[0]}</div>


                        <a onClick={this.createPage}>Create New</a>

                        <div className="createInput">
                            <form name="createPageForm" onSubmit={this.handleSubmit}>
                                title:<br></br>
                                <input value={title} type= "text"/>
                                <br></br>
                                content:<br></br>
                                <input value={content} type="textarea"/>
                                <br></br>

                                <button>submit</button>

                            </form>

                        </div>


                    </div>
                </div>
                <hr />

            </div>
        );
    }
}


export default Menu ;
