import React, { Component } from 'react';
import './menu.css';


/*Menu Component 
-------------------------------------------------*/

class Menu extends React.Component {
    constructor() {
        super();


        this.state = {

                title:[""],
                content:[""],  

                inputTitle:"",
                inputContent:"",

                nowPage:"",

        };

        this.handleChange = this.handleChange.bind(this);
        this.showCreatePage =this.showCreatePage.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.showThisPage = this.showThisPage.bind(this);
    }

    handleSubmit(e){
        e.preventDefault();

        var joinedTitle = this.state.title.concat(this.state.inputTitle);
        var joinedContent = this.state.content.concat(this.state.inputContent);

        this.setState({

           title: joinedTitle,
           content: joinedContent


        })


    }
    handleChange(e){
        e.preventDefault();
        const {name, value} = e.target;
        if(name == "title"){
            this.setState({
                inputTitle: value

            })
        }else{
            this.setState({
                inputContent: value

            })
        }

    }
    showCreatePage(e){
        e.preventDefault();
        this.setState({
            nowPage: "createPage"
        })

    }
    showThisPage(e){
        e.preventDefault();

        this.setState({
            nowPage: e.target.getAttribute('custom-attribute')
        })

    }

    render() {

        const { title, content, inputTitle, inputContent, nowPage} = this.state;



        return (
            <div>
                <div className="container mainPage">

                <div className="leftMenu col-3 col-lg-3 col-sm-3 ">
                     <ul>{title.map((item,key)=>
                    <li key={key} custom-attribute={key} onClick={this.showThisPage} >{item}</li>

                 )}</ul>
                 <a onClick={this.showCreatePage}>Create New</a>
                </div>


                    <div className={"createPage col-6 col-lg-6 col-sm-6 " + ((nowPage == "createPage" ) ? " selected" : " notSelected")}>
                        <div className="createInput">
                            <h2>create a new page</h2>
                            <form name="createPageForm" onSubmit={this.handleSubmit}>

                                title:<br></br>
                                <input onChange={this.handleChange} value={inputTitle} name="title" type= "text" required/>
                                <br></br>
                                content:<br></br>
                                <input onChange={this.handleChange} name="content" value={inputContent} type="textarea"/>
                                <br></br>

                                <button>submit</button>

                            </form>

                        </div>


                    </div>

                <div className={" contentPage col-6 col-lg-6 col-sm-6 " + ((nowPage == "createPage" ) ? " notSelected" : " selected")}> 
                 <h1>{title[nowPage]}</h1><br></br>
                 <p>{content[nowPage]}</p>

                </div>


                </div>
                <hr />

            </div>
        );
    }
}


export default Menu ;
