import React, { Component } from 'react';
import './menu.css';


/*Menu Component 
-------------------------------------------------*/

class Menu extends React.Component {
    constructor() {
        super();


        this.state = {

                title:[""],
                content:[""],  

                inputTitle:"",
                inputContent:"",

                nowPage:"createPage",

        };

        this.handleChange = this.handleChange.bind(this);
        this.showCreatePage =this.showCreatePage.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(e){
        e.preventDefault();

        var joinedTitle = this.state.title.concat(this.state.inputTitle);
        var joinedContent = this.state.content.concat(this.state.inputContent);

        this.setState({

           title: joinedTitle,
           content: joinedContent


        })


    }
    handleChange(e){
        e.preventDefault();
        const {name, value} = e.target;
        if(name == "title"){
            this.setState({
                inputTitle: value

            })
        }else{
            this.setState({
                inputContent: value

            })
        }

    }
    showCreatePage(){
        this.setState({
            nowPage: "createPage"
        })

    }

    render() {

        const { title, content, inputTitle, inputContent} = this.state;

        // const inputTitle = title.inputTitle;
        // const inputContent = title.inputContent;

        return (
            <div>
                <div className="container">

                <div className="col-3 col-lg-3 col-sm-3 ">
                     <ul>{title.map((item,key)=>
                    <li key={key} >{item}</li>

                 )}</ul>
                 <a onClick={this.showCreatePage}>Create New</a>
                </div>


                    <div className="createPage  selected col-6 col-lg-6 col-sm-6">
                        <div className="createInput">
                            <form name="createPageForm" onSubmit={this.handleSubmit}>

                                title:<br></br>
                                <input onChange={this.handleChange} value={inputTitle} name="title" type= "text"/>
                                <br></br>
                                content:<br></br>
                                <input onChange={this.handleChange} name="content" value={inputContent} type="textarea"/>
                                <br></br>

                                <button>submit</button>

                            </form>

                        </div>


                    </div>
                </div>
                <hr />

            </div>
        );
    }
}


export default Menu ;
