import React from 'react';
import { connect } from 'react-redux';

import { NavLink } from "react-router-dom";
import './menu.css'
import {store} from '../_helpers/store';

const selectedStyle = {
    backgroundColor: 'white',
    color: 'slategray'
  }
 const pagesArray = store.getState().createPageReducer;

  class Menu extends React.Component{


    render(){
        return(
            <nav className='main-menu col-md-4 col-md-offset-0'>

         { pagesArray.map((item, key) => 
           <NavLink key={item.id}  activeStyle = {selectedStyle} to={"/pages/"+ item.title +","+ item.message}> {item.title} </NavLink>


         )}


            <NavLink to='/createPage' activeStyle = {selectedStyle}>create new page</NavLink>
          </nav>


        )
    }
  }

  const mapStateToProps = (state) => {
    return {
        posts: state
    }
}
export default connect(mapStateToProps)(Menu);

  export {Menu};
