import React from 'react'
import {connect} from 'react-redux';
import { Link, Route } from "react-router-dom";

import {Template} from '../Template';
import { Menu } from '../Menu';


class CreatePage extends React.Component{ 

    constructor() {
        super();

        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(e){
        e.preventDefault();
        const title = this.getTitle.value;
        const message =  this.getMessage.value;
        const data = {
          id: new Date(),
          title,
          message
        }

        this.props.dispatch({
            type:'ADD_POST',
            data});
          this.getTitle.value = '';
          this.getMessage.value = '';


    }
    handleChange(e){
        e.preventDefault();
        const {name, value} = e.target;
        if(name == "title"){
            this.setState({
                inputTitle: value

            })
        }else{
            this.setState({
                inputContent: value

            })
        }

    }

    render() {
        const { title, content, inputTitle, inputContent, nowPage} = this.state;

        return (
            <Template>
            <div className={"createPage col-6 col-lg-6 col-sm-6 "}>
            <div >
                <h2>create page</h2>
                <form name="createPageForm" onSubmit={this.handleSubmit} >

                    title:<br></br>
                    <input ref={(input)=>this.getTitle = input} name="title" type= "text" required/>
                    <br></br>
                    content:<br></br>
                    <textarea   ref={(input)=>this.getMessage = input}   type="textarea"/>
                    <br></br>

                    <button>submit</button>

                </form>

            </div>
            </div>


            </Template>


        )
    }



}

export {CreatePage};
