import React, { Component } from 'react';


/*Menu Component 
-------------------------------------------------*/

class Menu extends React.Component {
    constructor() {
        super();


        this.state = {

                title:[""],
                content:[""],  

                inputTitle:"",
                inputContent:"",

             //add a state  nowPage 

        };

        this.handleChange = this.handleChange.bind(this);
        // this.createPage = this.createPage.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(e){
        e.preventDefault();

        var joinedTitle = this.state.title.concat(this.state.inputTitle);
        var joinedContent = this.state.content.concat(this.state.inputContent);

        this.setState({

           title: joinedTitle,
           content: joinedContent




        })


    }
    handleChange(e){
        e.preventDefault();
        const {name, value} = e.target;
        // this.setState({

        //    name: value

        //     //setState : push new value to pages array.

        // })
        this.setState(() => ({
            [name]: value
          }))

    }


    render() {

        const { title, content, inputTitle, inputContent} = this.state;

        // const inputTitle = title.inputTitle;
        // const inputContent = title.inputContent;

        return (
            <div>
                <div className="container">
                    <div className="text-center">


                    <div>{title.inputTitle}</div>


                        <a onClick={this.createPage}>Create New</a>

                        <div className="createInput">
                            <form name="createPageForm" onSubmit={this.handleSubmit}>
                                title:<br></br>
                                <input onChange={this.handleChange} value={inputTitle} name="title" type= "text"/>
                                <br></br>
                                content:<br></br>
                                <input onChange={this.handleChange} name="content" value={inputContent} type="textarea"/>
                                <br></br>

                                <button>submit</button>

                            </form>

                        </div>


                    </div>
                </div>
                <hr />

            </div>
        );
    }
}


export default Menu ;
