import React, { Component } from 'react';


/*Menu Component 
-------------------------------------------------*/

class Menu extends React.Component {
    constructor() {
        super();


        this.state = {

                title:[""],
                content:[""],  

                inputTitle:"",
                inputContent:"",

             //add a state  nowPage 

        };

        this.handleChange = this.handleChange.bind(this);
        // this.createPage = this.createPage.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(e){
        e.preventDefault();
        // const {name, value} = e.target;
        // var joined = this.state.title.concat(value);
        // this.setState({

        //    title: joined 

        //     //setState : push new value to pages array.

        // })


    }
    handleChange(e){
        e.preventDefault();
        const {name, value} = e.target;
        var joined = this.state.inputTitle.concat(value);
        this.setState({

           inputTitle: joined 

            //setState : push new value to pages array.

        })

    }


    render() {

        const { title, content} = this.state;
        const inputTitle = title.inputTitle;
        const inputContent = title.inputContent;

        return (
            <div>
                <div className="container">
                    <div className="text-center">


                    <div>{title}</div>


                        <a onClick={this.createPage}>Create New</a>

                        <div className="createInput">
                            <form name="createPageForm" onSubmit={this.handleSubmit}>
                                title:<br></br>
                                <input onChange={this.handleChange} value={inputTitle} name="title" type= "text"/>
                                <br></br>
                                content:<br></br>
                                <input onChange={this.handleChange} name="content" value={inputContent} type="textarea"/>
                                <br></br>

                                <button>submit</button>

                            </form>

                        </div>


                    </div>
                </div>
                <hr />

            </div>
        );
    }
}


export default Menu ;
