import React from 'react'
import { Link, Route } from "react-router-dom";

import {Template} from '../Template';
import { Menu } from '../Menu';


class CreatePage extends React.Component{ 

    constructor() {
        super();
        this.state = {

                title:[""],
                content:[""],  

                inputTitle:"",
                inputContent:"",

                nowPage:"",

        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(e){
        e.preventDefault();

        var joinedTitle = this.state.title.concat(this.state.inputTitle);
        var joinedContent = this.state.content.concat(this.state.inputContent);
        alert( joinedTitle);

        this.setState({

           title: joinedTitle,
           content: joinedContent


        })


    }
    handleChange(e){
        e.preventDefault();
        const {name, value} = e.target;
        if(name == "title"){
            this.setState({
                inputTitle: value

            })
        }else{
            this.setState({
                inputContent: value

            })
        }

    }

    render() {
        const { title, content, inputTitle, inputContent, nowPage} = this.state;

        return (
            <Template>
            <div className={"createPage col-6 col-lg-6 col-sm-6 "}>
            <div >
                <h2>create a new page</h2>
                <form name="createPageForm" onSubmit={this.handleSubmit} >

                    title:<br></br>
                    <input onChange={this.handleChange} value={inputTitle} name="title" type= "text" required/>
                    <br></br>
                    content:<br></br>
                    <input  onChange={this.handleChange} name="content" value={inputContent}  type="textarea"/>
                    <br></br>

                    <button>submit</button>

                </form>

            </div>
            </div>


            </Template>


        )
    }



}

export {CreatePage};
